import { useContext } from 'react'
import CartContext from '../../store/cart-context'
import Modal from '../UI/Modal'
import CartItem from './CartItem.js'
import styles from './Cart.module.css'

export default function Cart(props) {
    const cartCtx = useContext(CartContext)

    const cartItemRemoveHandler = (id) => {
        cartCtx.removeItem(id)
    }

    const cartItemAddHandler = (item) => {
        cartCtx.addItem({...item, amount: 1})
    }

    const cartItems = <ul className={styles['cart-items']}>
        {cartCtx.items.map((item) => (
            <CartItem 
                key={item.id}
                name={item.name}
                price={item.price}
                amount={item.amount}
                onRemove={cartItemRemoveHandler.bind(null, item.id)} // bind preconfigures the function to be called with the given arguments.
                onAdd={cartItemAddHandler.bind(null, item)}
            />
        ))}
    </ul>

    // trap focus in the modal
    const focusTrap = () => {
        const focusableElements = 'button, [href], input, select, textarea, [tabindex]:not([tabindex="-1"])'
        const modal = document.querySelector('overlays')
        const firstFocusableElement = modal.querySelectorAll(focusableElements)[0]
        const focusableContent = modal.querySelectorAll(focusableElements)
        const lastFocusableElement = focusableContent[focusableContent.length - 1]
        const KEYCODE_TAB = 9

        modal.addEventListener('keydown', function(e) {
            let isTabPressed = (e.key === 'Tab' || e.keyCode === KEYCODE_TAB)

            if (!isTabPressed) {
                return
            }

            if (e.shiftKey) {
                if (document.activeElement === firstFocusableElement) {
                    lastFocusableElement.focus()
                    e.preventDefault()
                }
            } else {
                if (document.activeElement === lastFocusableElement) {
                    firstFocusableElement.focus()
                    e.preventDefault()
                }
            }
        })
    }

    return (
        <Modal onClose={props.onClose}>
            <h2 onKeyDown={focusTrap}>Your Shopping Cart</h2>
            {cartItems}
            <div className={styles.total}>
                <span>Total Amount</span>
                <span>${cartCtx.totalAmount.toFixed(2)}</span>
            </div>
            <div className={styles.actions}>
                <button className={styles['button--alt']} onClick={props.onClose}>Close</button>
                <button className={styles.button}>Order</button>
            </div>
        </Modal>
    )
}
