const CheckoutInput = (props) => {
    return (
        <div className={props.style}>
            <label htmlFor={props.input.id}>{props.label}</label>
            <input type="text" id={props.input.id} ref={props.input.ref} />
            {!props.input.isValid && <p>Please enter a valid {props.label.toLowerCase()}.</p>}
        </div>
    )
}

export default CheckoutInput
